import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import './App.css';
import { LabelingUI } from './LabelingUI';
import LinearProgress from '@material-ui/core/LinearProgress';

import { createMuiTheme, MuiThemeProvider } from '@material-ui/core/styles';
import lightBlue from '@material-ui/core/colors/lightBlue';
import red from '@material-ui/core/colors/red';

export const primary = '#5495e3';
export const secondary = '#c1c1c1';
export const error = red.A700;
export const textColor = '#5b5b5b';
export const theme = createMuiTheme({
  palette: {
    primary: {
      ...lightBlue,
      A700: primary
    }
  }
});

const defaultState = {data: undefined, loading: true, label: undefined};

class App extends Component {
  state = defaultState;

  next(submission){
    this.setState(defaultState);
    const getNext = () => {
      window.Labelbox.fetchNextAssetToLabel();
    };
    if (!submission) {
      getNext();
    } else if (submission.label) {
      window.Labelbox.setLabelForAsset(JSON.stringify(submission.label || '')).then(getNext);
    } else if (submission.skip) {
      window.Labelbox.skip().then(getNext);
    }
  }

  componentWillMount(){
    window.Labelbox.currentAsset().subscribe((asset) => {
      if (!asset){
        this.setState({loading: true});
        return;
      }

      this.setState({data: asset.data, loading: false});
    });
  }

  render() {
    return (
      <MuiThemeProvider theme={theme}>
        <div className="App">
          <div className="Holder" >
            {this.state.loading && <LinearProgress/>}
            <div className={"LabelingFrame"}>
              <div className="LeftBarContainer" >
                <div className={"InstructionsBar"}>
                  <p>
                    1. Select a label category <br/>
                    2. Double click tiles on the image to apply label. <br/>
                    3. Click Submit to submit labels or skip to move to next image.
                  </p>
                  <p>
                    Scroll up and down over image or use controls to zoom in/out. Click and drag to
                    pan around image.
                  </p>
                  <p style={{ textColor: "red" }} >
                    Warning: Moving mouse while double-clicking leads to weird behavior
                  </p>
                </div>
                <div className={"ButtonBar"}>
                  <Button 
                    className="SkipButton"
                    variant="contained" 
                    color="secondary"
                    onClick={() => this.next({skip: true})}
                  >
                    Skip
                  </Button>
                  <Button
                    className="SubmitButton"
                    variant="contained"
                    color="primary"
                    disabled={!this.state.label}
                    onClick={() => this.next({label: this.state.label})}
                  >
                    Submit
                  </Button>
                </div>
              </div>

              <LabelingUI label={this.state.label} data={this.state.data} onLabelUpdate={(label) => this.setState({...this.state, label})} />


            </div>
          </div>
        </div>
      </MuiThemeProvider>
    );
  }
}

export default App;
